import React, { useState } from "react";

import { addCaregiver } from "../api";
import Button from "../components/Button";

export default function Rate() {


    return (
        <div>
            <form action="#" id="star_rating">
                <label className="container">One
                    <input type="radio" checked="checked"/>
                        <span className="checkmark"></span>
                </label>

                <label className="container">Two
                    <input type="radio"/>
                        <span className="checkmark"></span>
                </label>

                <label className="container">Three
                    <input type="radio"/>
                        <span className="checkmark"></span>
                </label>

                <label className="container">Four
                    <input type="radio"/>
                        <span className="checkmark"></span>
                </label>

                <button type="submit" className="btn-small visuallyhidden focusable">Submit
                    rating
                </button>

                                        <output></output>
            </form>
        </div>
    );
}
