import React from "react";
import {UserExtendedList} from "./User";
import {acceptReq, updateUser, useUser} from "../api";
import Loading from "../components/Loading";
import {Caregiver} from "./Caregivers";

export default function ToList() {
    /*var data = sessionStorage.getItem("listing")
    var dict_data = JSON.parse(data)
    console.log(data);*/
    const { loading, users, error} = useUser();
    if (loading) {
        return Loading();
    }
    if (error) {
        return <p>Something went wrong: {error.message}</p>;
    }
    let username;
    username = window.location.pathname;
    let index;
    index = username.lastIndexOf('/');
    let name;
    name = username.slice(index+1);
    let match;
    var dict_data;
    for (var user in users) {
        if (user.username === name) {
            dict_data = user;
            console.log(dict_data);
        }
    }



    if(dict_data.identity === "Caregiver") {
        return (
            <div>
                <h1>List</h1>
                <div className={"orderlist"}>
                    Order List
                    {dict_data.orderList.map(name => (
                        <p>
                            <button>
                                {name}
                            </button>
                        </p>
                    ))}
                </div>
                <div className={"rec_req_list"}>
                    Receive List
                    {dict_data.receiveReq.map(name => (
                        <p>
                            <button>
                                {name}
                            </button>
                            <button onClick={() => accept(name)}>
                                accept
                            </button>
                            <button>
                                reject
                            </button>
                        </p>
                    ))}
                </div>
                <div className={"history_list"}>
                    History List
                    {dict_data.orderHistory.map(name => (
                        <p>
                            <button>
                                {name}
                            </button>
                        </p>
                    ))}
                </div>
            </div>
        );
    }else{
        return (
            <div>
                <h1>List</h1>
                <div className={"orderlist"}>
                Order List
                {dict_data.orderList.map(name => (
                    <p>
                        <button>
                            {name}
                        </button>
                    </p>
                ))}
            </div>
                <div className={"send_req_list"}>
                    Send List
                    {dict_data.sentReq.map(name => (
                        <p>
                            <button>
                                {name}
                            </button>
                        </p>
                    ))}
                </div>
                <div className={"history_list"}>
                    History List
                    {dict_data.orderHistory.map(name => (
                        <p>
                            <button>
                                {name}
                            </button>
                        </p>
                    ))}
                </div>
            </div>
        );
    }
}

function accept(name){
    let username;
    username = window.location.pathname;
    let index;
    index = username.lastIndexOf('/');
    username = username.slice(index+1);
    acceptReq({
        username: username,
        name: name,
        state: "accept",
    });
    //window.location.assign(`http://localhost:3000/toList/${window.sessionStorage.getItem("username")}`)

}
