import React, {useEffect, useState} from "react";
import { NavLink } from "react-router-dom";
import Button from "./Button";
export default function Nav() {
  return (
    <nav>
      <NavLink exact to="/">
        Home
      </NavLink>
      <NavLink to="/login">Login</NavLink>
      <NavLink to="/findCaregivers">Caregivers</NavLink>
        <Button className={"btn-logIn"} onClick={showUser()}>{window.sessionStorage.getItem("username")}</Button>
    </nav>
  );
}

function showUser() {
    const [username, setUsername] = useState(window.sessionStorage.getItem("username"));
    useEffect(() => {
        if (username) {
            return (
                window.location.href = `user-management/${window.sessionStorage.getItem("username")}`
            );
        }
    }, [])
}
